import { Component, OnInit } from '@angular/core';

// app.component.ts
import { KeyValuePipe } from '@angular/common';
import { HttpClient } from '@angular/common/http';
import { map, Observable } from 'rxjs';
import {DomSanitizer} from '@angular/platform-browser';

@Component({
  selector: 'app-lists',
  templateUrl: './schemes.component.html',
  styleUrls: ['./schemes.component.scss'],
  providers: [KeyValuePipe]
})
export class ListComponent implements OnInit {

  listOfDogs: any = [];
  mySelect = [];
  arrayObject1 : object[] = [];
  selectedValue: any;
  multiple = false;
  dataUser: any;
  pics : any;

  dogsData : any;
  dogsData2 : any;
  dogsData3 : any;
  valuesD: any;
  breedsArray1 : any = [];
  itemImageUrl : any = [];
  wikiUrl : any;

  constructor(
    private keyValue: KeyValuePipe,
    private http: HttpClient,
    private sanitizer: DomSanitizer
  ) {}

  ngOnInit() {
    this.dogDataFetcher();
  }

  dogDataFetcher() {
    const promise = this.fetchListOfDogs();
    const promise2 = this.fetchImage();
    promise.then((dataP1) => {
      promise2.then((data2) =>{
      this.listOfDogs = dataP1;
      console.log(`dane 2 ${data2.message}`)
      console.log(this.listOfDogs)
      this.itemImageUrl = this.sanitizer.bypassSecurityTrustUrl(data2);
    })});
    console.log(this.listOfDogs);
  }

  $dogsBreed(): Observable<any> {
    return this.http.get<any>("https://dog.ceo/api/breeds/list/all")
  }

  wikiLinkAdd(){
    this.wikiUrl = `https://en.wikipedia.org/${this.mySelect[0]}`;
  }

  async fetchImage() {
    try {
      let imgURL = `https://dog.ceo/api/breed/${this.mySelect[0]}/images/random`;
      console.log("Rodzaj");
      console.log(this.mySelect[0]);
      const response = await fetch(imgURL);
      const data = await response.json();
      console.log('dane:')
      console.log(data.message);
      return data.message;
    }
    catch (error) {
      console.error(`Could not get products: ${error}`);
      return 0;
    }
  }

  getBreedsPic(): Observable<any> {
    return this.http.get<any>(`https://dog.ceo/api/breed/hound/images`)
    }

  fetchImage2() {
    this.getBreedsPic()
        .subscribe(response => {
        this.pics = response.message;
        return this.pics;
    });
  }


  async fetchListOfDogs() {
    try {
      const response = await fetch('https://dog.ceo/api/breeds/list/all');
      const data = await response.json();
      const data2 = Object.keys(data.message);
      return data2;
    }
    catch (error) {
      console.error(`Could not get products: ${error}`);
      return 0;
    }
  }
}
